example.programlisting:
#! /bin/sh # # log4sh example: Hello, world # myDir=`dirname $0`
# find and source log4sh if [ -r "$myDir/log4sh" ]; then log4shDir=$myDir elif [ -r "./log4sh" ]; then log4shDir=. else echo "fatal: could not find log4sh" >&2 exit 1 fi . $log4shDir/log4sh
# say Hello to the world logger_info "Hello, world"
